                      =, <nowiki/>{{#time: H:i|{{{1|00:00}}}|{{#if: {{{lang|}}} | {{{lang}}} | {{int:lang}} }} }}
  |ckb|ks|arabic-indic=, <nowiki/>{{#time: H:i|{{{1|00:00}}}|ks }}  <!-- arabic-indic language not handled by {{#time}} (same as ks)  -->
  |fa|glk|mzn|ur      =, <nowiki/>{{#time: H:i|{{{1|00:00}}}|fa }}  <!-- ur language not handled by {{#time}} (same as fa)  -->
  |sl                 =&nbsp;ob <nowiki/>{{#time: H.i|{{{1|00:00}}} }}   <!-- sl language (prefers 10.30 notation)  -->
  |#default           =, <nowiki/>{{#time: H:i|{{{1|00:00}}} }}     <!-- all other languages (use 10:30 notation)  -->

